query getManagerApprovals {
  accessRequestApprovalSurveyQuestions(
    filters: {workflowStep: {filterValue: "ManagerApproval", operator: EQUALS}}
  ) {
    pages
    total
    data {
      reason
      surveyId
      surveyObjectKey
      workflowStep
      context {
        displayName
        type
        id
      }
    }
  }
}

